import React from 'react'
import "./Filter.css"
import {useRef} from 'react'

export default function CheckBoxComponent({value}) {
  const checkboxRef=useRef(null)

  const handleClick=()=>{
    if(checkboxRef.current){
      checkboxRef.current.click();
    }
  }

  return (
    <div className='checkboxComponentDiv' onClick={handleClick}>
      <input type="checkbox" className='checks' ref={checkboxRef}/><span className='checkboxComponents'>{value}</span>
    </div>
  )
}
